import { Component, OnInit, ViewChild } from '@angular/core';
import { FullCalendarComponent } from '@fullcalendar/angular';
import { EventInput } from '@fullcalendar/core';
import dayGridPlugin from '@fullcalendar/daygrid';

@Component({
  selector: 'app-see-calendar',
  templateUrl: './see-calendar.component.html',
  styleUrls: ['./see-calendar.component.css']
})
export class SeeCalendarComponent implements OnInit {
  @ViewChild('calendar') calendarDiv!: FullCalendarComponent;
  options: any;
  eventsModel: any[] = [];
  calendarVisible = true;
  calendarWeekends = true;
  calendarEvents: EventInput[] = [
    { title: 'Event Now', start: new Date() }
  ];
  toggleVisible() {
    this.calendarVisible = !this.calendarVisible;
  }
  ngOnInit() {
    this.options = {
      editable: false,
      disableDragging: false,
      selectable: true,
      theme: 'standart',
      header: {
        right: 'prev,next, today',
        left: '',
      },
      validRange: {
        start: '2017-05-01',
        end: '2019-06-01'
      },
      plugins: [dayGridPlugin]
    };
    this.calendarEvents = [{
      title: 'Updaten Event',
      start: '2020-08',
      end: '2020-10'
    }];
  }
  toggleWeekends() {
    this.calendarWeekends = !this.calendarWeekends;
  }
  eventClick(model: any) {
  }
  dateClick(model: any) {
  }
  eventDragStop(model: any) {
  }
  gotoPast() {
    let calendarApi = this.calendarDiv.getApi();
    calendarApi.gotoDate('2000-01-01'); // call a method on the Calendar object  
  }

  handleDateClick(arg) {
    if (confirm('Would you like to add an event to ' + arg.dateStr + ' ?')) {
      this.calendarEvents = this.calendarEvents.concat({ // add new event data. must create new array  
        title: 'New Event',
        start: arg.date,
        allDay: arg.allDay
      })
    }
  }

}
<div class="main">
  <full-calendar #calendar [editable]="true" [events]="calendarEvents"
                 [header]="options.header" [customButtons]="options.customButtons"
                 (dateClick)="dateClick($event)" (eventDragStop)="eventDragStop($event)"
                 [plugins]="options.plugins" (eventClick)="eventClick($event)"
                 [columnHeaderHtml]="options.columnHeaderHtml">
  </full-calendar>
</div>
